import React from "react"

import Maze from "./Maze"
import generateMaze from "./generateMaze"

import "./App.css"

// const maze = generateMaze({width: 40, height: 40})

export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      width: 0,
      height: 0
    }
  }

  componentDidMount() {
    const { width, height } = this.state;

  }

  handleChange(event) {
    this.setState({width: event.target.width});
    this.setState({height: event.target.height});
  }

  handleSubmit(event) {
    const {width, height} = this.state;
    const maze = generateMaze({width, height})
    event.preventDefault();
  }

  render(){
    return (
    <div className="App">
      <form onSubmit={this.handleSubmit}>
        <label>
          hauteur:
          <input type="text" value={this.state.width} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
        <label>
          largeur:
          <input type="text" value={this.state.height} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <Maze maze={this.state.width, this.state.height} />
    </div>
    )
  }
}
