import React from "react"

import * as images from "./images"
import "./Maze.css"

function entryAndExit(indexRow, indexCell, cell){
  if(indexRow === 0){
    return <img src={indexCell === 1 ? images.stairsUp : images.wall} alt="" />
  } else if(indexRow === 20) {
    return <img src={indexCell === 1 ? images.stairsUp : images.wall} alt="" />
  } else {
    return <img src={indexCell === 1 ? images.stairsUp : images.wall} alt="" />
  }
