}


function Maze({ maze }) {
  console.log(maze.tiles)
  return (
    <div className="Maze">
      {maze.tiles.map((row, indexRow) => (
        <div className="Maze-row">
          {row.map((cell, indexCell) => (
            entryAndExit(indexRow, indexCell, cell)
          ))}
        </div>
      ))}
    </div>
  )
}

export default Maze
