import React from "react"

import Maze from "./Maze"
import generateMaze from "./generateMaze"

import "./App.css"

const maze = generateMaze({width: 40, height: 40})

export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      width: 0,
      height: 0
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    const { width, height } = this.state;
    generateMaze({width: 40, height: 40})

  }

  handleChange(event) {
    this.setState({width: event.target.value});
    this.setState({height: event.target.value});
  }

  handleSubmit(event) {
    const {width, height} = this.state;
    const maze = generateMaze({width, height})
    event.preventDefault();
  }

  render(){
    return (
    <div className="App">
      <form onSubmit={this.handleSubmit}>
        <label>
          hauteur:
          <input type="text" value={this.state.width} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
        <label>
          largeur:
          <input type="text" value={this.state.height} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <Maze maze={maze} />
    </div>
    )
  }
}
