  switch (maze) {
    case maze[0]:
      return(
        <img src={indexCell === 1 ? images.stairsUp : images.wall} alt="" />
      )
    case 20:
      return(
        <img src={indexCell === 19 ? images.stairsDown : images.wall} alt="" />
      )
    default:
      return(
        <img src={cell.passable ? images.floor : images.wall} alt="" />
      )
  }
}


